mapper.update:
update bongsa
   set bs_ing = '진행중'
 where bs_no = #{bs_no}

update bongsa
   set bs_ing = '불가함'
 where bs_no = #{bs_no}

mapper.select:
select *
from ( select * from bongsa order by bs_recruit_end desc)
where rownum <= 10


select VT_TITLE, VT_NAME, VT_USER_PHONE, VT_USER_EMAIL
from VOLUNTEER 
where bs_no=#{bs_no}
