mapper.update:
update funding_pj
   set fd_read_cnt = fd_read_cnt+1
 where fd_no = #{fd_no}

update funding_pj
   set fd_status = '진행중'
 where fd_no = #{fd_no}

update funding_pj
   set fd_status = '진행중'
 where fd_no = #{fd_no}

update funding_pj
   set fd_status = '불가함'
 where fd_no = #{fd_no}

mapper.select:
select *
from ( select * from funding_pj order by fd_read_cnt desc)
where rownum <= 10
