mapper.update:
UPDATE REVIEW
   SET review_read_cnt=review_read_cnt +1
 WHERE review_no=#{review_no}

UPDATE REVIEW
SET review_title=#{review_title}, review_text=#{review_text}
WHERE review_no=#{review_no}

mapper.select:
select *
from ( select * from review order by review_read_cnt desc)
where rownum <= 10
